import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import VehicleModal from './VehicleModal';
import { 
  Search, 
  Filter, 
  Grid, 
  List, 
  ChevronDown, 
  Heart, 
  Eye, 
  Calendar, 
  Gauge, 
  Fuel, 
  Users,
  MapPin,
  Star,
  ArrowUpDown,
  X
} from 'lucide-react';

const Inventory = () => {
  const vehicles = [
    {
      id: 1,
      images: [
        'data:image/jpeg;base64,'
      ],
      make: 'Porsche',
      model: '911 Turbo S',
      year: 2023,
      price: 850000,
      originalPrice: 900000,
      mileage: 2500,
      fuel: 'Petrol',
      transmission: 'Automatic',
      bodyType: 'Coupe',
      seats: 2,
      engine: '3.8L Twin-Turbo',
      power: '640 HP',
      location: 'Dubai',
      condition: 'New',
      features: ['Carbon Fiber Package', 'Sport Chrono', 'PASM', 'Ceramic Brakes'],
      isNew: true,
      featured: true,
      rating: 4.9,
      views: 1250
    },
    {
      id: 2,
      images: [
        'data:image/jpeg;base64,'
      ],
      make: 'Aston Martin',
      model: 'DB11',
      year: 2022,
      price: 1200000,
      mileage: 8500,
      fuel: 'Petrol',
      transmission: 'Automatic',
      bodyType: 'Coupe',
      seats: 4,
      engine: '4.0L Twin-Turbo V8',
      power: '503 HP',
      location: 'Abu Dhabi',
      condition: 'Excellent',
      features: ['Bang & Olufsen Audio', 'Carbon Fiber Trim', 'Adaptive Suspension'],
      isNew: false,
      featured: true,
      rating: 4.8,
      views: 890
    },
    {
      id: 3,
      images: [
        'data:image/jpeg;base64,'
      ],
      make: 'Bentley',
      model: 'Continental GT',
      year: 2023,
      price: 950000,
      mileage: 1200,
      fuel: 'Petrol',
      transmission: 'Automatic',
      bodyType: 'Coupe',
      seats: 4,
      engine: '6.0L W12',
      power: '626 HP',
      location: 'Dubai',
      condition: 'New',
      features: ['Mulliner Package', 'Naim Audio', 'Massage Seats', 'Panoramic Roof'],
      isNew: true,
      featured: true,
      rating: 4.9,
      views: 1100
    },
    {
      id: 4,
      images: [
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRYo7Y3slRsdKzD990PiDsc7zaWdIaY6a8MtA&s'
      ],
      make: 'Ferrari',
      model: 'F8 Tributo',
      year: 2022,
      price: 1650000,
      mileage: 3800,
      fuel: 'Petrol',
      transmission: 'Automatic',
      bodyType: 'Coupe',
      seats: 2,
      engine: '3.9L Twin-Turbo V8',
      power: '710 HP',
      location: 'Dubai',
      condition: 'Excellent',
      features: ['Carbon Fiber Aero', 'Racing Seats', 'Track Package'],
      isNew: false,
      featured: true,
      rating: 5.0,
      views: 1580
    },
    {
      id: 5,
      images: [
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTglI2nqbPOaOT5yIbw1WUIPpGyyNMpi0_ZiA&s'
      ],
      make: 'McLaren',
      model: '720S',
      year: 2021,
      price: 1500000,
      mileage: 12000,
      fuel: 'Petrol',
      transmission: 'Automatic',
      bodyType: 'Coupe',
      seats: 2,
      engine: '4.0L Twin-Turbo V8',
      power: '710 HP',
      location: 'Sharjah',
      condition: 'Very Good',
      features: ['MSO Package', 'Carbon Fiber Body', 'Track Telemetry'],
      isNew: false,
      featured: false,
      rating: 4.7,
      views: 750
    },
    {
      id: 6,
      images: [
        'https://images.pexels.com/photos/2127733/pexels-photo-2127733.jpeg?auto=compress&cs=tinysrgb&w=800'
      ],
      make: 'Lamborghini',
      model: 'Hurac√°n EVO',
      year: 2023,
      price: 1300000,
      mileage: 500,
      fuel: 'Petrol',
      transmission: 'Automatic',
      bodyType: 'Coupe',
      seats: 2,
      engine: '5.2L V10',
      power: '630 HP',
      location: 'Dubai',
      condition: 'New',
      features: ['Performance Package', 'Carbon Interior', 'Lifting System'],
      isNew: true,
      featured: false,
      rating: 4.9,
      views: 920
    }
  ];

  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [sortBy, setSortBy] = useState('featured');
  const [filteredVehicles, setFilteredVehicles] = useState(vehicles);
  const [selectedVehicle, setSelectedVehicle] = useState<typeof vehicles[0] | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filters, setFilters] = useState({
    search: '',
    make: '',
    model: '',
    yearMin: '',
    yearMax: '',
    priceMin: '',
    priceMax: '',
    mileageMax: '',
    fuelType: '',
    transmission: '',
    bodyType: '',
    condition: '',
    location: ''
  });
  const location = useLocation();

  const makes = [...new Set(vehicles.map(v => v.make))];
  const bodyTypes = [...new Set(vehicles.map(v => v.bodyType))];
  const locations = [...new Set(vehicles.map(v => v.location))];

  // Initialize filters from URL parameters
  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const newFilters = { ...filters };
    
    if (searchParams.get('search')) {
      newFilters.search = searchParams.get('search') || '';
    }
    if (searchParams.get('priceMin')) {
      newFilters.priceMin = searchParams.get('priceMin') || '';
    }
    if (searchParams.get('priceMax')) {
      newFilters.priceMax = searchParams.get('priceMax') || '';
    }
    if (searchParams.get('location')) {
      newFilters.location = searchParams.get('location') || '';
    }
    
    setFilters(newFilters);
  }, [location.search]);

  // Filter vehicles based on current filters
  useEffect(() => {
    let filtered = [...vehicles];

    // Search filter
    if (filters.search) {
      const searchTerm = filters.search.toLowerCase();
      filtered = filtered.filter(vehicle => 
        vehicle.make.toLowerCase().includes(searchTerm) ||
        vehicle.model.toLowerCase().includes(searchTerm) ||
        vehicle.year.toString().includes(searchTerm) ||
        `${vehicle.make} ${vehicle.model}`.toLowerCase().includes(searchTerm)
      );
    }

    // Make filter
    if (filters.make) {
      filtered = filtered.filter(vehicle => vehicle.make === filters.make);
    }

    // Model filter
    if (filters.model) {
      filtered = filtered.filter(vehicle => vehicle.model.toLowerCase().includes(filters.model.toLowerCase()));
    }

    // Year filters
    if (filters.yearMin) {
      filtered = filtered.filter(vehicle => vehicle.year >= parseInt(filters.yearMin));
    }
    if (filters.yearMax) {
      filtered = filtered.filter(vehicle => vehicle.year <= parseInt(filters.yearMax));
    }

    // Price filters
    if (filters.priceMin) {
      filtered = filtered.filter(vehicle => vehicle.price >= parseInt(filters.priceMin));
    }
    if (filters.priceMax) {
      filtered = filtered.filter(vehicle => vehicle.price <= parseInt(filters.priceMax));
    }

    // Mileage filter
    if (filters.mileageMax) {
      filtered = filtered.filter(vehicle => vehicle.mileage <= parseInt(filters.mileageMax));
    }

    // Fuel type filter
    if (filters.fuelType) {
      filtered = filtered.filter(vehicle => vehicle.fuel === filters.fuelType);
    }

    // Transmission filter
    if (filters.transmission) {
      filtered = filtered.filter(vehicle => vehicle.transmission === filters.transmission);
    }

    // Body type filter
    if (filters.bodyType) {
      filtered = filtered.filter(vehicle => vehicle.bodyType === filters.bodyType);
    }

    // Condition filter
    if (filters.condition) {
      filtered = filtered.filter(vehicle => vehicle.condition === filters.condition);
    }

    // Location filter
    if (filters.location) {
      filtered = filtered.filter(vehicle => vehicle.location === filters.location);
    }

    // Apply sorting
    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'year-new':
        filtered.sort((a, b) => b.year - a.year);
        break;
      case 'mileage-low':
        filtered.sort((a, b) => a.mileage - b.mileage);
        break;
      case 'featured':
      default:
        filtered.sort((a, b) => {
          if (a.featured && !b.featured) return -1;
          if (!a.featured && b.featured) return 1;
          return 0;
        });
        break;
    }

    setFilteredVehicles(filtered);
  }, [filters, sortBy]);

  const handleFilterChange = (key: string, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      make: '',
      model: '',
      yearMin: '',
      yearMax: '',
      priceMin: '',
      priceMax: '',
      mileageMax: '',
      fuelType: '',
      transmission: '',
      bodyType: '',
      condition: '',
      location: ''
    });
  };

  const formatPrice = (price: number) => {
    return `AED ${price.toLocaleString()}`;
  };

  const formatMileage = (mileage: number) => {
    return `${mileage.toLocaleString()} km`;
  };

  const handleViewDetails = (vehicle: typeof vehicles[0]) => {
    setSelectedVehicle(vehicle);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedVehicle(null);
  };

  return (
    <section className="py-20 bg-black min-h-screen">
      <div className="max-w-7xl mx-auto px-4">
        {/* Header */}
        <div className="mb-12">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
            Vehicle <span className="text-yellow-500">Inventory</span>
          </h1>
          <p className="text-xl text-zinc-400">
            Discover our complete collection of premium vehicles
          </p>
        </div>

        {/* Search and Controls */}
        <div className="bg-zinc-900 rounded-2xl p-6 mb-8">
          <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
            {/* Search Bar */}
            <div className="flex-1 relative">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-zinc-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search by make, model, or keyword..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
                className="w-full bg-zinc-800 text-white pl-12 pr-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
              />
            </div>

            {/* Controls */}
            <div className="flex items-center space-x-4">
              {/* Sort */}
              <div className="relative">
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500 appearance-none pr-10"
                >
                  <option value="featured">Featured First</option>
                  <option value="price-low">Price: Low to High</option>
                  <option value="price-high">Price: High to Low</option>
                  <option value="year-new">Year: Newest First</option>
                  <option value="mileage-low">Mileage: Low to High</option>
                </select>
                <ArrowUpDown className="absolute right-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4 pointer-events-none" />
              </div>

              {/* View Mode */}
              <div className="flex bg-zinc-800 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 rounded ${viewMode === 'grid' ? 'bg-yellow-500 text-black' : 'text-zinc-400'}`}
                >
                  <Grid className="w-5 h-5" />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 rounded ${viewMode === 'list' ? 'bg-yellow-500 text-black' : 'text-zinc-400'}`}
                >
                  <List className="w-5 h-5" />
                </button>
              </div>

              {/* Filter Toggle */}
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="bg-zinc-800 text-white px-4 py-3 rounded-lg hover:bg-zinc-700 transition-colors flex items-center space-x-2"
              >
                <Filter className="w-5 h-5" />
                <span>Filters</span>
                <ChevronDown className={`w-4 h-4 transition-transform ${showFilters ? 'rotate-180' : ''}`} />
              </button>
            </div>
          </div>

          {/* Advanced Filters */}
          {showFilters && (
            <div className="mt-6 pt-6 border-t border-zinc-700">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <select
                  value={filters.make}
                  onChange={(e) => handleFilterChange('make', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                >
                  <option value="">All Makes</option>
                  {makes.map(make => (
                    <option key={make} value={make}>{make}</option>
                  ))}
                </select>

                <select
                  value={filters.bodyType}
                  onChange={(e) => handleFilterChange('bodyType', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                >
                  <option value="">All Body Types</option>
                  {bodyTypes.map(type => (
                    <option key={type} value={type}>{type}</option>
                  ))}
                </select>

                <select
                  value={filters.condition}
                  onChange={(e) => handleFilterChange('condition', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                >
                  <option value="">All Conditions</option>
                  <option value="New">New</option>
                  <option value="Excellent">Excellent</option>
                  <option value="Very Good">Very Good</option>
                  <option value="Good">Good</option>
                </select>

                <select
                  value={filters.location}
                  onChange={(e) => handleFilterChange('location', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                >
                  <option value="">All Locations</option>
                  {locations.map(location => (
                    <option key={location} value={location}>{location}</option>
                  ))}
                </select>

                <input
                  type="number"
                  placeholder="Min Year"
                  value={filters.yearMin}
                  onChange={(e) => handleFilterChange('yearMin', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                />

                <input
                  type="number"
                  placeholder="Max Year"
                  value={filters.yearMax}
                  onChange={(e) => handleFilterChange('yearMax', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                />

                <input
                  type="number"
                  placeholder="Min Price (AED)"
                  value={filters.priceMin}
                  onChange={(e) => handleFilterChange('priceMin', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                />

                <input
                  type="number"
                  placeholder="Max Price (AED)"
                  value={filters.priceMax}
                  onChange={(e) => handleFilterChange('priceMax', e.target.value)}
                  className="bg-zinc-800 text-white px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
                />
              </div>

              <div className="flex justify-between items-center mt-4">
                <button
                  onClick={clearFilters}
                  className="text-zinc-400 hover:text-white transition-colors flex items-center space-x-2"
                >
                  <X className="w-4 h-4" />
                  <span>Clear All Filters</span>
                </button>
                <div className="text-zinc-400">
                  Showing {filteredVehicles.length} of {vehicles.length} vehicles
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Vehicle Grid/List */}
        <div className={viewMode === 'grid' 
          ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8' 
          : 'space-y-6'
        }>
          {filteredVehicles.length > 0 ? (
            filteredVehicles.map((vehicle) => (
            <div key={vehicle.id} className={`group bg-zinc-900 rounded-2xl overflow-hidden hover:transform hover:scale-105 transition-all duration-500 ${
              viewMode === 'list' ? 'flex' : ''
            }`}>
              {/* Vehicle Image */}
              <div className={`relative overflow-hidden ${viewMode === 'list' ? 'w-80 flex-shrink-0' : ''}`}>
                <img 
                  src={vehicle.images[0]} 
                  alt={`${vehicle.make} ${vehicle.model}`}
                  className={`object-cover group-hover:scale-110 transition-transform duration-700 ${
                    viewMode === 'list' ? 'w-full h-full' : 'w-full h-64'
                  }`}
                />
                
                {/* Badges */}
                <div className="absolute top-4 left-4 flex flex-col space-y-2">
                  {vehicle.isNew && (
                    <span className="bg-green-500 text-white px-3 py-1 rounded-full text-sm font-semibold">
                      New
                    </span>
                  )}
                  {vehicle.featured && (
                    <span className="bg-yellow-500 text-black px-3 py-1 rounded-full text-sm font-semibold">
                      Featured
                    </span>
                  )}
                </div>

                {/* Action Buttons */}
                <div className="absolute top-4 right-4 flex flex-col space-y-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <button className="bg-black/70 text-white p-2 rounded-full hover:bg-yellow-500 hover:text-black transition-colors">
                    <Heart className="w-5 h-5" />
                  </button>
                  <button className="bg-black/70 text-white p-2 rounded-full hover:bg-yellow-500 hover:text-black transition-colors">
                    <Eye className="w-5 h-5" />
                  </button>
                </div>

                {/* Price Overlay */}
                <div className="absolute bottom-4 left-4">
                  <div className="bg-black/80 backdrop-blur-sm text-white px-4 py-2 rounded-lg">
                    <span className="text-2xl font-bold text-yellow-500">{formatPrice(vehicle.price)}</span>
                    {vehicle.originalPrice && vehicle.originalPrice > vehicle.price && (
                      <div className="text-sm text-zinc-400 line-through">
                        {formatPrice(vehicle.originalPrice)}
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Vehicle Details */}
              <div className="p-6 flex-1">
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className="text-2xl font-bold text-white mb-2">
                      {vehicle.make} {vehicle.model}
                    </h3>
                    <p className="text-zinc-400 font-medium">{vehicle.year}</p>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Star className="w-4 h-4 text-yellow-500 fill-current" />
                    <span className="text-white font-semibold">{vehicle.rating}</span>
                    <span className="text-zinc-400 text-sm">({vehicle.views} views)</span>
                  </div>
                </div>

                {/* Specifications */}
                <div className="grid grid-cols-2 gap-4 mb-6">
                  <div className="flex items-center space-x-2 text-zinc-300">
                    <Gauge className="w-4 h-4 text-yellow-500" />
                    <span className="text-sm">{formatMileage(vehicle.mileage)}</span>
                  </div>
                  <div className="flex items-center space-x-2 text-zinc-300">
                    <Fuel className="w-4 h-4 text-yellow-500" />
                    <span className="text-sm">{vehicle.fuel}</span>
                  </div>
                  <div className="flex items-center space-x-2 text-zinc-300">
                    <Calendar className="w-4 h-4 text-yellow-500" />
                    <span className="text-sm">{vehicle.transmission}</span>
                  </div>
                  <div className="flex items-center space-x-2 text-zinc-300">
                    <Users className="w-4 h-4 text-yellow-500" />
                    <span className="text-sm">{vehicle.seats} Seats</span>
                  </div>
                </div>

                {/* Engine & Location */}
                <div className="mb-4">
                  <div className="text-zinc-300 text-sm mb-2">
                    <strong className="text-white">{vehicle.engine}</strong> ‚Ä¢ {vehicle.power}
                  </div>
                  <div className="flex items-center space-x-2 text-zinc-400 text-sm">
                    <MapPin className="w-4 h-4" />
                    <span>{vehicle.location}</span>
                    <span>‚Ä¢</span>
                    <span className="text-green-400">{vehicle.condition}</span>
                  </div>
                </div>

                {/* Features */}
                {viewMode === 'list' && (
                  <div className="mb-4">
                    <h4 className="text-white font-semibold mb-2">Key Features:</h4>
                    <div className="flex flex-wrap gap-2">
                      {vehicle.features.slice(0, 3).map((feature, index) => (
                        <span key={index} className="bg-zinc-800 text-zinc-300 px-3 py-1 rounded-full text-sm">
                          {feature}
                        </span>
                      ))}
                      {vehicle.features.length > 3 && (
                        <span className="text-yellow-500 text-sm">+{vehicle.features.length - 3} more</span>
                      )}
                    </div>
                  </div>
                )}

                {/* Action Buttons */}
                <div className="flex space-x-3">
                  <button 
                    onClick={() => handleViewDetails(vehicle)}
                    className="flex-1 bg-yellow-500 text-black py-3 rounded-lg font-semibold hover:bg-yellow-400 transition-colors"
                  >
                    View Details
                  </button>
                  <button className="flex-1 border border-zinc-600 text-white py-3 rounded-lg font-semibold hover:bg-zinc-800 transition-colors">
                    Contact Seller
                  </button>
                </div>
              </div>
            </div>
            ))
          ) : (
            <div className="col-span-full text-center py-16">
              <div className="text-zinc-400 text-xl mb-4">No vehicles found matching your criteria</div>
              <p className="text-zinc-500 mb-8">Try adjusting your search filters or browse our complete inventory</p>
              <button 
                onClick={clearFilters}
                className="bg-yellow-500 text-black px-6 py-3 rounded-lg font-semibold hover:bg-yellow-400 transition-colors"
              >
                Clear All Filters
              </button>
            </div>
          )}
        </div>

        {/* Load More */}
        {filteredVehicles.length > 0 && (
          <div className="text-center mt-12">
          <button className="bg-transparent border-2 border-yellow-500 text-yellow-500 px-8 py-4 rounded-lg font-semibold text-lg hover:bg-yellow-500 hover:text-black transition-colors">
            Load More Vehicles
          </button>
          </div>
        )}
      </div>

      {/* Vehicle Modal */}
      {selectedVehicle && (
        <VehicleModal
          vehicle={selectedVehicle}
          isOpen={isModalOpen}
          onClose={handleCloseModal}
        />
      )}
    </section>
  );
};

export default Inventory;